package de.uniorg.ui5helper.ui5;

import com.google.gson.JsonParser;
import groovy.util.GroovyTestCase;

public class ClassDocumentationTest extends GroovyTestCase {
    private static final String SUBJECT = "{\"kind\": \"class\", \"name\": \"sap.m.ActionListItem\", \"basename\": \"ActionListItem\", \"resource\": \"sap/m/ActionListItem.js\", \"module\": \"sap/m/ActionListItem\", \"static\": true, \"visibility\": \"public\", \"extends\": \"sap.m.ListItemBase\", \"description\": \"The <code>sap.m.ActionListItem</code> can be used like a <code>button</code> to fire actions when pressed.\\n<b>Note:</b> The inherited <code>selected</code> property of the <code>sap.m.ListItemBase</code> is not supported.\", \"ui5-metadata\": {\"stereotype\": \"control\", \"properties\": [{\"name\": \"text\", \"type\": \"string\", \"defaultValue\": null, \"group\": \"Misc\", \"visibility\": \"public\", \"description\": \"Defines the text that appears in the control.\", \"methods\": [\"getText\", \"setText\"] }] }, \"constructor\": {\"visibility\": \"public\", \"parameters\": [{\"name\": \"sId\", \"type\": \"string\", \"optional\": true, \"description\": \"Id for the new control, generated automatically if no id is given\"}, {\"name\": \"mSettings\", \"type\": \"object\", \"optional\": true, \"description\": \"Initial settings for the new control\"}], \"description\": \"Constructor for a new ActionListItem.\\n\\n\\nAccepts an object literal <code>mSettings</code> that defines initial\\nproperty values, aggregated and associated objects as well as event handlers.\\nSee {@link sap.ui.base.ManagedObject#constructor} for a general description of the syntax of the settings object.\"}, \"methods\": [{\"name\": \"extend\", \"visibility\": \"public\", \"static\": true, \"returnValue\": {\"type\": \"function\", \"description\": \"Created class / constructor function\"}, \"parameters\": [{\"name\": \"sClassName\", \"type\": \"string\", \"optional\": false, \"description\": \"Name of the class being created\"}, {\"name\": \"oClassInfo\", \"type\": \"object\", \"optional\": true, \"description\": \"Object literal with information about the class\"}, {\"name\": \"FNMetaImpl\", \"type\": \"function\", \"optional\": true, \"description\": \"Constructor function for the metadata object; if not given, it defaults to <code>sap.ui.core.ElementMetadata</code>\"}], \"description\": \"Creates a new subclass of class sap.m.ActionListItem with name <code>sClassName</code>\\nand enriches it with the information contained in <code>oClassInfo</code>.\\n\\n<code>oClassInfo</code> might contain the same kind of information as described in {@link sap.m.ListItemBase.extend}.\"}, {\"name\": \"getMetadata\", \"visibility\": \"public\", \"static\": true, \"returnValue\": {\"type\": \"sap.ui.base.Metadata\", \"description\": \"Metadata object describing this class\"}, \"description\": \"Returns a metadata object for class sap.m.ActionListItem.\"}, {\"name\": \"getMode\", \"visibility\": \"protected\", \"description\": \"Determines item specific mode\\n\\nActionListItems are not selectable because they are command controls (like Button or Link) so triggering the associated command, rather than selection is\\nappropriate to happen upon user action on these items. By overwriting isSelectable (inherited from ListItemBase) we exclude the item from processing\\nspecific to selectable list-items.\"}, {\"name\": \"getText\", \"visibility\": \"public\", \"returnValue\": {\"type\": \"string\", \"description\": \"Value of property <code>text</code>\"}, \"description\": \"Gets current value of property <code>text</code>.\\n\\nDefines the text that appears in the control.\"}, {\"name\": \"setText\", \"visibility\": \"public\", \"returnValue\": {\"type\": \"sap.m.ActionListItem\", \"description\": \"Reference to <code>this</code> in order to allow method chaining\"}, \"parameters\": [{\"name\": \"sText\", \"type\": \"string\", \"optional\": false, \"description\": \"New value for property <code>text</code>\"}], \"description\": \"Sets a new value for property <code>text</code>.\\n\\nDefines the text that appears in the control.\\n\\nWhen called with a value of <code>null</code> or <code>undefined</code>, the default value of the property will be restored.\"}] }";

    public void testGetName() {
        String name = getSubject().getName();
        assertEquals("sap.m.ActionListItem", name);
    }

    private ClassDocumentation getSubject() {
        return ClassDocumentation.fromJsonDoc((new JsonParser()).parse(SUBJECT).getAsJsonObject());
    }
}
